INSERT INTO scenarios (name, category, description, keywords, instructions) VALUES
('Severe Bleeding', 'Physical Injuries', 'Heavy and uncontrolled bleeding, often caused by a deep wound or injury.', '{injury, bleeding}', '1. Call for emergency medical help \n 2. Apply direct pressure to the wound \n 3. Elevate the affected limb \n 4. Use a tourniquet as a last resort \n 5. Monitor breathing and provide CPR if necessary.'),
('Seizure', 'Neurological Emergencies', 'A sudden and uncontrolled electrical disturbance in the brain, leading to altered behavior and loss of consciousness.', '{seizure, emergency}', '1. Clear the area around the person \n 2. Protect the person from injury \n 3. Place the person on their side \n 4. Do not restrain the person \n 5. Call for emergency medical help if the seizure lasts longer than 5 minutes.'),
('Panic Attack', 'Mental Health Emergencies', 'An intense and sudden episode of fear or anxiety, often accompanied by physical symptoms such as sweating and rapid heart rate.', '{panic, anxiety, emergency}', '1. Encourage the person to focus on their breathing \n 2. Offer a quiet and calming environment \n 3. Reassure the person that they are safe \n 4. Do not leave the person alone \n 5. Call for emergency medical help if the symptoms do not subside.'),
('Choking', 'Respiratory Emergencies', 'Obstruction of the airway, often caused by a piece of food or other object.', '{choking, emergency}', '1. Ask the person if they are choking \n 2. Encourage the person to cough \n 3. Administer back blows and abdominal thrusts if necessary \n 4. Call for emergency medical help if the person cannot breathe or loses consciousness \n 5. Monitor breathing and provide CPR if necessary.'),
('Drug Overdose', 'Toxicological Emergencies', 'Excessive use or ingestion of drugs, leading to potentially life-threatening symptoms such as respiratory depression.', '{overdose, drugs, emergency}', '1. Call for emergency medical help \n 2. Monitor breathing and provide CPR if necessary \n 3. Administer naloxone if the overdose is opioid-related \n 4. Keep the person calm and comfortable \n 5. Identify the substance and amount ingested if possible.')