INSERT INTO scenarios (name, category, description, keywords, instructions) VALUES
('Allergic Reaction', 'Allergies', 'A severe reaction to an allergen, such as a food or insect bite.', '{allergies, reaction}', '1. Call for emergency medical help \n 2. Administer epinephrine if available \n 3. Monitor breathing and provide CPR if necessary.'),
('Broken Arm', 'Physical Injuries', 'A fracture in one of the bones in the arm, often caused by trauma.', '{injury, arm, fracture}', '1. Call for emergency medical help \n 2. Immobilize the arm with a splint or sling \n 3. Apply ice to reduce swelling \n 4. Administer pain medication as needed.'),
('Heart Attack', 'Cardiac Emergencies', 'A blockage in the arteries that supply blood to the heart, leading to damage or death of heart muscle tissue.', '{heart, attack, emergency}', '1. Call for emergency medical help \n 2. Have the person chew and swallow an aspirin if not allergic \n 3. Administer CPR if necessary\n4. Use an automated external defibrillator (AED) if available.'),
('Asthma Attack', 'Respiratory Emergencies', 'A sudden onset of wheezing, shortness of breath, and chest tightness, often caused by exposure to allergens or irritants.', '{asthma, attack, emergency}', '1. Have the person sit upright and stay calm \n 2. Administer a rescue inhaler if available \n 3. Call for emergency medical help if the inhaler does not work \n 4. Administer oxygen if available.'),
('Heat Exhaustion', 'Environmental Emergencies', 'A condition caused by prolonged exposure to high temperatures, often resulting in dehydration and electrolyte imbalances.', '{heat, exhaustion, dehydration}', '1. Move the person to a cool, shaded area \n 2. Offer water or sports drinks to rehydrate \n 3. Apply cool, wet towels to the skin \n 4. Monitor for signs of heat stroke.'),
('Sprained Ankle', 'Physical Injuries', 'An injury to the ligaments in the ankle, often caused by a sudden twist or turn.', '{injury, ankle, sprain}', '1. Rest the affected ankle \n 2. Elevate the ankle above heart level \n 3. Apply ice to reduce swelling \n 4. Compress the ankle with a bandage \n 5. Administer pain medication as needed.'),
('Stroke', 'Neurological Emergencies', 'A blockage or rupture of blood vessels in the brain, leading to loss of brain function and potentially life-threatening complications.', '{stroke, emergency}', '1. Call for emergency medical help \n 2. Note the time the symptoms started \n 3. Administer aspirin if not allergic \n 4. Monitor breathing and provide CPR if necessary \n 5. Keep the person calm and comfortable.'),
('Poisoning', 'Toxicological Emergencies', 'Exposure to a harmful substance, such as a chemical or medication, leading to potentially life-threatening symptoms.', '{poisoning, emergency}', '1. Call for emergency medical help \n 2. Identify the substance and amount ingested \n 3. Administer activated charcoal if available \n 4. Monitor breathing and provide CPR if necessary \n 5. Keep the person calm and comfortable.');